from flask import Flask,render_template,request
app = Flask(__name__)
Lists = [
    {   
        "model" : "Honda",
        "daily_fee" :"100000",
        "Image": "data:image/jpeg;base64,",
        "Year": "2010",
    },
    {   
        "model" : "Yamaha",
        "daily_fee" :"200000",
        "Image": "https://file.chodocu.com/crop/422x340/2017/09/18/2719030a-5b7f-4555-b-475a.jpg",
        "Year":2015,
    },
]
@app.route("/")
def a ():
    return render_template("all_bkie.html", iteam_lists = Lists)

@app.route("/run", methods = ["GET" , "POST"])
def r ():
    if request.method == "GET":
        return render_template("add_bike.html")
    elif request.method == "POST":
        form = request.form
        m = form["Model"]
        d = form["Daily_fee"]
        i = form["img"]
        y = form["year"]
        new_lists = {
            "model" : m,
            "daily_fee":d,
            "Image" :i,
            "Year":y,
        }
        Lists.append(new_lists)
        return m+ " " +str(d)+" " + i+" " +str(y)
if __name__ == "__main__":
    app.run(port = 5000,debug = True )

